import React, { Component } from "react";
import { retrieveAllFavorite } from "./getData";
import "../style/Best.css";

class Collection extends Component {
    constructor(props) {
        super(props);
        this.state = {
            peronalCollection: [],
        };
    }

    componentDidMount() {
        const name = window.localStorage.getItem('user');
        const array = [];

        retrieveAllFavorite(name).then((res) => {
            console.log(res);
            res.map((obj) => {
                array.push(obj.favorite_recipe_id);
                console.log(obj.favorite_recipe_id);
            })

            for (let i = 0; i < array.length; i++) {
                const getSingleRecipeInfo = async (recipeID) => {
                    const res = await axios.get(
                        `http://localhost:8080/singlerecipeinfo/${JSON.stringify(recipeID)}`
                    );
                    return res.data;
                };
            }
        })


    }

    render() {

        return (
            <div>

            </div>
        );
    }
}

export default Collection;
