import React, { Component } from "react";
import PropTypes from "prop-types";
import { getRecommendAuthorsBasedonPopularity } from "./getData";

class Best extends Component {
    constructor(props) {
        super(props);
        this.state = {
            rankListState: [],
        };
    }
    componentDidMount() {
        getRecommendAuthorsBasedonPopularity().then((res) => {
            console.log(res);
            let num = 0;
            const rankingList = res.map((person) => {
                num += 1;
                return (
                    <tr id="ranklist">
                        <td style={{ marginTop: "40px", marginLeft: "70px", marignBottom: "0px", color: "white", fontSize: "20px" }}>{`${num}`}</td>
                        <td><button style={{ background: "none", border: "none", color: "white", fontSize: "20px" }}>{person["Author"]} </button></td>
                        <td style={{ marginTop: "40px", marginLeft: "70px", marignBottom: "0px", color: "white", fontSize: "20px" }}>{person["avg(b.Rate)"]}</td>
                    </tr>
                )

            })
            this.setState({ rankListState: rankingList })

        });
    }

    render() {

        return (
            <div>
                <div id="leaderBoard">
                    <h3>Top 20 Recepie Author</h3>
                    <table id="table">
                        <tbody>
                            <tr>
                                <th>Ranking</th>
                                <th>Author</th>
                                <th>Avg Rating</th>
                            </tr>
                            <>{this.state.rankListState}</>
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default Best;
