import React, { Component } from "react";
import PageNavbar from "./PageNavbar";
import Ingredient from "./Ingredient";
import { fetchIngredient } from "./getData";

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredient: "",
      ingredientArray: [],
      ingrendientComponents: [],
    };

    this.handleSendImg = this.handleSendImg.bind(this);
    this.handleSubmitIngredientInput = this.handleSubmitIngredientInput.bind(
      this
    );
  }

  handleSubmitIngredientInput() {
    let { ingredientArray, ingredient, ingrendientComponents } = this.state;
    console.log(ingredient);
    if (ingredient != "") {
      fetchIngredient(ingredient).then((res) => {
        ingredientArray.push({ food: { ingredient }, img: { res } });

        // const ingedientObj = ingredientArray.map((object, j) => {
        const ingedientObj = (
          <Ingredient
            name={ingredientArray[0].food}
            html={ingredientArray[0].img}
          />
        );
        // });
        // console.log(ingedientObj);
        this.setState({ ingrendientComponents, ingedientObj });
      });
    }

    document.getElementById("IngredientInput").value = "";
  }

  handleSendImg(e) {
    e.preventDefault();
    this.setState({ [e.target.name]: e.target.value });
    // console.log(this.state.ingredient);
  }

  render() {
    const { ingrendientComponents } = this.state;
    return (
      <div>
        <PageNavbar />
        <input
          id="IngredientInput"
          type="text"
          name="ingredient"
          onChange={(e) => this.handleSendImg(e)}
        />
        <button
          type="submit"
          onClick={() => this.handleSubmitIngredientInput()}
        >
          Add
        </button>
        <div>{ingrendientComponents}</div>
      </div>
    );
  }
}

export default Search;
