import React, { Component } from "react";
import { getSingleRecipeInfo, retrieveAllFavorite } from "./getData";
import "../style/Best.css";

class Collection extends Component {
    constructor(props) {
        super(props);
        this.state = {
            peronalCollection: [],
            collections: []
        };
    }

    componentDidMount() {
        const name = window.localStorage.getItem('user');
        const array = [];
        const objArray = [];


        retrieveAllFavorite(name).then((res) => {
            console.log(res);
            res.map((obj) => {
                array.push(obj.favorite_recipe_id);
                console.log(obj.favorite_recipe_id);
            })

            for (let i = 0; i < array.length;) {
                getSingleRecipeInfo(array[i]).then((res) => {
                    const name = res[0]["Recipe Name"];
                    const photo = res[0]["Recipe Name"]
                    objArray.push({ "name": name, "photo": photo });
                    i++;
                    if (i < array.length) {
                        const collectionsTemp = objArray.map((obj) => {
                            console.log(obj);
                            console.log(obj);
                            return (
                                <div>
                                    <div>{obj[name]}</div>
                                    <img src={obj[photo]} />
                                </div>
                            )
                        })
                        this.setState({ collections: collectionsTemp })
                    }
                }
                );

            }


        })


    }

    render() {

        return (
            <div>
                {this.state.collections}
            </div>
        );
    }
}

export default Collection;
