            <div id="recommendAuthorTime" style={{ marginTop: "40px", marginLeft: "5%", marignBottom: "0px"}}>
              {this.state.recommendBaseOnIngredient}
            </div>
          </div>
        </div>
      </body >
    );
  }
}

