import React, { Component } from "react";
import { getSingleRecipeIngredient } from "./getData";

class SingleRecipe extends Component {
  constructor(props) {
    super(props);

    this.state = {
      theRecepie: "",
      username: "",
      regidate: "",
    };
  }

  componentDidMount() {
    const urlArray = window.location.href.split("/");
    const recipeIDTemp = urlArray[urlArray.length - 1];
    const recipeID = decodeURI(recipeIDTemp);
    console.log(recipeID);
    this.setState({ theRecepie: recipeID });
    getSingleRecipeIngredient(recipeID);
  }

  render() {
    const { theRecepie, ingredients } = this.state;
    return (
      <div>
        <div>{theRecepie}</div>
        <div>{ingredients}</div>
      </div>
    );
  }
}

export default SingleRecipe;
