import React, { Component } from "react";
import { getSingleRecipeInfo, retrieveAllFavorite } from "./getData";
import "../style/Best.css";

class Collection extends Component {
    constructor(props) {
        super(props);
        this.state = {
            peronalCollection: [],
        };
    }

    componentDidMount() {
        const name = window.localStorage.getItem('user');
        const array = [];
        const nameArray = [];
        const photoArray = [];

        retrieveAllFavorite(name).then((res) => {
            console.log(res);
            res.map((obj) => {
                array.push(obj.favorite_recipe_id);
                console.log(obj.favorite_recipe_id);
            })

            for (let i = 0; i < array.length; i++) {
                getSingleRecipeInfo(array[i]).then((res) => {
                    nameArray.push(res[0]["Recipe Name"]);
                    photoArray.push(res[0]["Recipe Name"]);
                }
                );
            }
        })


    }

    render() {

        return (
            <div>

            </div>
        );
    }
}

export default Collection;
