import React, { Component } from "react";
class SingleRecipe extends Component {
  constructor(props) {
    super(props);

    this.state = {
      theRecepie: "",
      username: "",
      regidate: "",
    };
  }

  componentDidMount() {
    const urlArray = window.location.href.split("/");

    const recepie = urlArray[urlArray.length - 1];
    const x = decodeURI(recepie);
    console.log(x);
    this.setState({ theRecepie: recepie });
  }
  render() {
    return <div>{this.state.theRecepie}</div>;
  }
}

export default SingleRecipe;
