/* eslint-disable no-unused-vars */
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ButtonBase from '@material-ui/core/ButtonBase';
import PropTypes from 'prop-types';
import image0 from "../images/Picture5.png";

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%'

  },
  paper: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: 500,
  },
  image: {
    width: 128,
    height: 128,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
}));

/**
 * The example data is structured as follows:
 *
 * import image from 'path/to/image.jpg';
 * [etc...]
 *
 * const tileData = [
 *   {
 *     img: image,
 *     title: 'Image',
 *     author: 'author',
 *   },
 *   {
 *     [etc...]
 *   },
 * ];
 */

function RecipeList(props) {
  const classes = useStyles();
  const {recipeInfo} = props;
  console.log(recipeInfo.recipeInfo);

  return (
    <div className={classes.root}>
      {recipeInfo.map((recipe) => ( 
        <Grid container spacing={0} style={{backgroundColor:"white", height:300}}>
          <Grid item xs={3}>
            <ButtonBase className={classes.image} style={{width:"100%", height:"80%", margin:"2%"}}>
              <img className={classes.img} alt="complex" src={recipe.RecipePhoto} />
            </ButtonBase>
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={0} style={{backgroundImage:`url(${image0})`, backgroundSize:"cover"}}>
              <Grid item xs>
                <Typography gutterBottom variant="subtitle1">
                  Standard license
                </Typography>
                <Typography variant="body2" gutterBottom>
                  Full resolution 1920x1080 â€¢ JPEG
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  ID: 1030114
                </Typography>
              </Grid>
              <Grid item>
                <Typography variant="body2" style={{ cursor: 'pointer' }}>
                  Remove
                </Typography>
              </Grid>
            </Grid>
            {/* <Grid item>
              <Typography variant="subtitle1">$19.00</Typography>
            </Grid> */}
          </Grid>
        </Grid>   
      ))}
    </div>
  );
}

RecipeList.propTypes = {
    recipeInfo: PropTypes.arrayOf(
        PropTypes.shape({ 
            keyword: PropTypes.string.isRequired,
            Author: PropTypes.string.isRequired,
            Directions: PropTypes.string.isRequired,
            Ingredients: PropTypes.string.isRequired,
            RecipeName: PropTypes.string.isRequired,
            RecipePhoto: PropTypes.string.isRequired,
            RecipeID: PropTypes.number.isRequired,
            TotalTime: PropTypes.string.isRequired,
        }),
    )
};
RecipeList.defaultProps= {
    recipeInfo:[]
}

export default RecipeList;
